import React from 'react';

// import { BrowserRouter , Route , Routes } from 'react-router-dom';

import Nav from "./home/nav";
import Header from "./home/header";
import Reports from "./home/reports";
import Section from "./home/section";
import Advanced from "./home/sec_3_advanced";
import Menu from "./home/Menu";
import imga from "./home/img/01.jpg"
import img_1 from "./home/img/1.png";

import './style/index.css';


class App extends React.Component {
  render(){
    const infromation_menu = [
      {id:"4" , img : "./home/img/01.jpg" , tittle_1 : "Rainbow Vegetable Sandwich" , tittle_2 : "Time: 15 - 20 Minutes | Serves: 1"},
      {id:"5" , img : "./home/img/02.jpg" , tittle_1 : "Vegetarian Burger" , tittle_2 : "Time: 20 - 25 Minutes | Serves: 1"},
      {id:"6" , img : "./home/img/01.jpg" , tittle_1 : "RaRaspberry Stuffed French Toast" , tittle_2 : "Time: 25 - 30 Minutes | Serves: 1"},
    ];

    const All_Data_menu = infromation_menu.map((mem)=>{
        return(
          <Menu
            key = {mem.id}
            img = {mem.img}
            tittle_1 = {mem.tittle_1}
            tittle_2 = {mem.tittle_2}

          />)
        // console.log(mem.tittle_1)
      })


    return (
      <div>
        {/* the navghtion menu */}
        <Nav />
        <img  src={img_1} />
        {/* the Header */}
        <Header />

        {/* 34tion- one Reports */}
        < Reports />

        {/* section- one section */}
        < Section />

        {/*section Advanced */}
        <Advanced />

        {/*section Advanced */}
        {All_Data_menu}    
      </div>
    );
}}

export default App;
